import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

export default function OrderDetails() {
  const { orderId } = useParams();
  const navigate = useNavigate();

  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadOrder = async () => {
      try {
        const res = await axios.get(
          `http://localhost:9169/api/orders/order/${orderId}`
        );

        console.log("ORDER LOADED:", res.data); // DEBUG
        setOrder(res.data);
      } catch (err) {
        console.error("ORDER LOAD ERROR:", err);
        alert("Failed to load order details");
      } finally {
        setLoading(false);
      }
    };

    loadOrder();
  }, [orderId]);

  if (loading) return <p>Loading...</p>;
  if (!order) return <p>Order not found</p>;

  return (
    <div className="min-h-screen bg-[#B197A4] p-6">
      <div className="max-w-xl mx-auto bg-[#dfdfe2] p-6 rounded-md shadow-md">
        <h1 className="text-2xl font-bold mb-2">{order.restaurantName}</h1>

        <p><strong>Order ID:</strong> {order.orderId}</p>
        <p><strong>Date:</strong> {new Date(order.orderDate).toLocaleString()}</p>
        <p><strong>Status:</strong> {order.status}</p>

        <h2 className="font-semibold mt-4">Items:</h2>
        <ul>
          {order.items.map((item, index) => (
            <li key={index} className="flex justify-between py-2 border-b">
              <span>{item.item} × {item.qty}</span>
              <span>${item.price}</span>
            </li>
          ))}
        </ul>

        <p className="font-bold text-lg mt-4">Total: ${order.totalPrice}</p>

        <button
          onClick={() => alert("Order confirmed!")}
          className="mt-4 bg-[#6b6363] text-white px-6 py-2 rounded-md border-2 border-black"
        >
          CONFIRM ORDER
        </button>
      </div>
    </div>
  );
}

