import React, { useEffect, useState } from "react";
import axios from "axios";

export default function OrderHistory() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/orders")
      .then((res) => setOrders(res.data))
      .catch((err) => console.log(err));
  }, []);

  const handleReorder = (restaurantId) => {
    console.log("Reordering:", restaurantId);
    // you can add reorder logic later
  };

  return (
    <div className="min-h-screen bg-[#c6b0bb] p-6">
      <h1 className="text-3xl font-bold mb-6 text-black">ORDER HISTORY</h1>

      <div className="space-y-4">
        {orders.map((order) => (
          <div
            key={order._id}
            className="restaurant-card bg-[#d3d3d3] p-4 rounded-md font-[Comic_Sans_MS] border border-[#b8a4b0]"
          >
            {/* Restaurant Name */}
            <h3 className="text-lg font-bold mb-1">{order.restaurantName}</h3>

            {/* Order Date */}
            <p className="text-sm mb-1">{new Date(order.date).toDateString()}</p>

            {/* Price */}
            <p className="font-semibold mb-3">${order.totalPrice}</p>

            {/* Reorder Button */}
            <button
              onClick={() => handleReorder(order.restaurantId)}
              className="bg-white px-4 py-2 rounded-sm font-mono font-semibold hover:bg-[#b08ea3] hover:text-white transition"
            >
              REORDER
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
