import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

export default function OrderDetails() {
  const { orderId } = useParams();
  const navigate = useNavigate();

  const [order, setOrder] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:9169/api/orders/details/${orderId}`)
      .then((res) => setOrder(res.data))
      .catch(() => alert("Failed to load order details"));
  }, [orderId]);

  const confirmOrder = async () => {
    try {
      await axios.post("http://localhost:9169/api/orders/reorder", {
        previousOrderId: orderId,
      });
      alert("ORDER CONFIRMED!");
      navigate("/orders");
    } catch (err) {
      alert("Failed to confirm order");
    }
  };

  if (!order) return <p>Loading...</p>;

  return (
    <div className="min-h-screen bg-[#B197A4] p-6">
      <div className="max-w-xl mx-auto bg-[#dfdfe2] p-6 rounded-md shadow-md">
        <h1 className="text-2xl font-bold mb-4">
          {order.restaurantName}
        </h1>

        <h2 className="font-semibold">Items:</h2>
        <ul className="mb-4">
          {order.items.map((item, i) => (
            <li key={i} className="flex justify-between border-b py-2">
              <span>{item.item} Ã— {item.qty}</span>
              <span>${item.price}</span>
            </li>
          ))}
        </ul>

        <p className="font-bold text-lg">Total: ${order.totalPrice}</p>

        <button
          onClick={confirmOrder}
          className="mt-5 bg-[#6b6363] text-white px-6 py-2 rounded-md border-2 border-black"
        >
          CONFIRM ORDER
        </button>
      </div>
    </div>
  );
}
