import React, { useEffect, useState } from "react";
import OrderCard from "../components/OrderCard";
const navigate = useNavigate();
const OrderHistory = () => {
  const [orders, setOrders] = useState([]);

  // Fetch order history from backend
  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/orders/USER001");
        const data = await res.json();
        setOrders(data);
      } catch (err) {
        console.error("Error fetching orders:", err);
      }
    };

    fetchOrders();
  }, []);

  const handleReorder = async (order) => {
    alert(`Reordering: ${order.restaurantName}`);
    // Call your reorder API endpoint here
  };

  return (
    <div className="min-h-screen bg-[#b99eaa] p-6">
      {/* Top Header */}
      <div className="flex items-center justify-between">
        <div className="text-3xl font-extrabold tracking-wide">ORDER HISTORY</div>
      </div>

      {/* Order Cards */}
      <div className="mt-6 space-y-5">
        {orders.length > 0 ? (
          orders.map((order) => (
            <OrderCard
              key={order.orderId}
              order={order}
              onReorder={() => handleReorder(order)}
            />
          ))
        ) : (
          <p className="text-lg text-gray-700">Loading orders...</p>
        )}
      </div>
    </div>
  );
};

export default OrderHistory;
