import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";


export default function OrderHistory() {
const [orders, setOrders] = useState([]);
const navigate = useNavigate();


const userId = "USER001"; // replace with logged‑in user id


useEffect(() => {
axios
.get(`http://localhost:9169/api/orders/${userId}`)
.then((res) => setOrders(res.data))
.catch((err) => console.log(err));
}, []);


const handleReorder = async (order) => {
try {
await axios.post("http://localhost:9169/api/orders/reorder", {
previousOrderId: order._id,
});
alert("Order placed again!");
} catch (err) {
console.log(err);
}
};


return (
<div className="min-h-screen bg-[#b99fab] p-8 font-sans">{/* wrapper */}
{/* Title */}
<h1 className="text-3xl font-bold mb-6 text-black">ORDER HISTORY</h1>


{/* Orders */}
<div className="space-y-6">
{orders.map((order) => (
<div
key={order._id}
className="bg-[#dfdfe2] p-6 rounded-lg shadow-md flex justify-between items-center"
>
{/* Left content */}
<div>
<h2 className="text-lg font-bold text-black">{order.restaurantName}</h2>
<p className="text-sm text-gray-700 mt-1">
{new Date(order.orderDate).toLocaleDateString()}
</p>
<p className="mt-2 font-semibold text-black">${order.totalPrice}</p>
</div>


{/* Reorder button */}
<button
onClick={() => handleReorder(order)}
className="px-6 py-2 bg-gray-400 text-black font-semibold rounded hover:bg-gray-500"
>
REORDER
</button>
</div>
))}
</div>
</div>
);
}
