import React from "react";
import "./FilterBar.css";

const FilterBar = ({ dataset = [], cuisine, setCuisine, rating, setRating, location, setLocation }) => {
  const cuisines = [...new Set(dataset.map((r) => r.cuisine))];
  const ratings = [...new Set(dataset.map((r) => r.rating))].sort((a, b) => a - b);
  const locations = [...new Set(dataset.map((r) => r.location))];

  return (
    <div className="filter-bar flex items-center justify-between rounded-xl mx-auto mt-10 mb-10 bg-[#D9D9D9] px-4 gap-20" style={{ width: "500px", height: "50px", fontFamily: "Itim", fontSize: "28px" }}>
      <select value={cuisine} onChange={(e) => setCuisine(e.target.value)} className="bg-transparent outline-none text-black uppercase" style={{ fontFamily: "Itim", fontSize: "20px" }}>
        <option value="">CUISINE</option>
        {cuisines.map((c) => <option key={c} value={c}>{c}</option>)}
      </select>

      <select value={rating} onChange={(e) => setRating(e.target.value)} className="bg-transparent outline-none text-black uppercase" style={{ fontFamily: "Itim", fontSize: "20px" }}>
        <option value="">RATINGS</option>
        {ratings.map((r) => <option key={r} value={r}>{r}+</option>)}
      </select>

      <select value={location} onChange={(e) => setLocation(e.target.value)} className="bg-transparent outline-none text-black uppercase" style={{ fontFamily: "Itim", fontSize: "20px" }}>
        <option value="">LOCATIONS</option>
        {locations.map((l) => <option key={l} value={l}>{l}</option>)}
      </select>
    </div>
  );
};

export default FilterBar;
