// src/pages/OrderHistory.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import OrderCard from "../components/OrderCard";
import "./OrderHistory.css";

export default function OrderHistory() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  // TODO: replace with actual logged-in user id later
  const userId = "USER001";

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await axios.get(`http://localhost:9169/api/orders/${userId}`);
        // depending on your backend the response may be array or object; handle both:
        setOrders(Array.isArray(res.data) ? res.data : res.data.orders || []);
      } catch (err) {
        console.error("Failed to fetch orders:", err);
        setOrders([]);
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  // attempts reorder using multiple possible endpoints (safe fallback)
  const handleReorder = async (order) => {
    try {
      // try variant 1: POST /api/orders/reorder/:orderId
      await axios.post(`http://localhost:9169/api/orders/reorder/${order._id}`);
      alert("Reorder placed (method 1).");
      return;
    } catch (e1) {
      // fallback 2: POST /api/orders/:id/reorder
      try {
        await axios.post(`http://localhost:9169/api/orders/${order._id}/reorder`);
        alert("Reorder placed (method 2).");
        return;
      } catch (e2) {
        // fallback 3: POST /api/orders/reorder with previousOrderId body
        try {
          await axios.post(`http://localhost:9169/api/orders/reorder`, {
            previousOrderId: order._id,
          });
          alert("Reorder placed (method 3).");
          return;
        } catch (e3) {
          console.error("Reorder failed (all attempts):", e1, e2, e3);
          alert("Reorder failed. Check console and backend.");
        }
      }
    }
  };

  return (
    <div className="order-history" style={{ backgroundColor: "#B197A4", minHeight: "100vh", padding: "20px" }}      >>
      <div className="max-w-6xl mx-auto">
        <h1 className="text-3xl font-bold mb-6 text-black">ORDER HISTORY</h1>

        {loading ? (
          <p className="text-lg text-gray-800">Loading orders...</p>
        ) : orders.length === 0 ? (
          <p className="text-lg text-gray-800">No orders found.</p>
        ) : (
          <div className="space-y-6">
            {orders.map((order) => (
              <OrderCard key={order._id} order={order} onReorder={() => handleReorder(order)} />
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
