import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

export default function OrderDetails() {
  const { orderId } = useParams();

  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [confirming, setConfirming] = useState(false);

  useEffect(() => {
    const loadOrder = async () => {
      try {
        const res = await axios.get(
          `http://localhost:9169/api/orders/order/${orderId}`
        );
        console.log("ORDER LOADED:", res.data);
        setOrder(res.data);
      } catch (err) {
        console.error("ORDER LOAD ERROR:", err);
        alert("Failed to load order details");
      } finally {
        setLoading(false);
      }
    };

    loadOrder();
  }, [orderId]);

  const handleReorder = async () => {
    try {
      setConfirming(true);
      await axios.post(
        `http://localhost:9169/api/orders/reorder/${order._id}`
      );
      alert("Order confirmed!");
    } catch (err) {
      console.error("Reorder failed:", err);
      alert("Failed to reorder");
    } finally {
      setConfirming(false);
    }
  };

  if (loading)
    return (
      <p className="loading-text" style={{ paddingTop: "40px" }}>
        Loading...
      </p>
    );

  if (!order)
    return (
      <p className="loading-text" style={{ paddingTop: "40px" }}>
        Order not found
      </p>
    );

  return (
    <div
      className="order-history"
      style={{
        backgroundColor: "#B197A4",
        minHeight: "100vh",
        padding: "20px",
      }}
    >
      <div
        className="max-w-3xl mx-auto bg-[#dfdfe2] p-8 rounded-xl shadow-lg"
        style={{
          border: "2px solid black",
        }}
      >
        <h1 className="text-3xl font-bold mb-4 text-black">
          ORDER DETAILS
        </h1>

        <h2 className="text-xl font-semibold text-black mb-2">
          {order.restaurantName}
        </h2>

        <div className="mb-4 text-black">
          <p><strong>Order ID:</strong> {order.orderId}</p>
          <p>
            <strong>Date:</strong>{" "}
            {new Date(order.orderDate).toLocaleString()}
          </p>
          <p><strong>Status:</strong> {order.status}</p>
        </div>

        <h3 className="font-semibold text-lg text-black mb-2">Items:</h3>

        <ul className="bg-white rounded-md p-4 border border-black">
          {order.items.map((item, index) => (
            <li
              key={index}
              className="flex justify-between py-2 border-b last:border-none text-black"
            >
              <span>
                {item.item} Ã— {item.qty}
              </span>
              <span>${item.price}</span>
            </li>
          ))}
        </ul>

        <p className="font-bold text-xl mt-4 text-black">
          Total: ${order.totalPrice}
        </p>

        <button
          onClick={handleReorder}
          disabled={confirming}
          className={`mt-6 w-full px-6 py-3 rounded-md border-2 border-black text-white text-lg font-semibold 
            ${confirming ? "bg-gray-500" : "bg-[#6b6363]"}`}
        >
          {confirming ? "Confirming..." : "REORDER"}
        </button>
      </div>
    </div>
  );
}
