// export default RestaurantListing;
import React, { useEffect, useState } from "react";
import axios from "axios";
import RestaurantCard from "../components/RestaurantCard";
import FilterBar from "../components/FilterBar";

const RestaurantListing = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [cuisine, setCuisine] = useState("");
  const [rating, setRating] = useState("");
  const [location, setLocation] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchRestaurants = async () => {
    setLoading(true);
    setError(null);

    try {
      // Only send query params if a filter is selected
      const params = {};
      if (cuisine) params.cuisine = cuisine;
      if (rating) params.rating = parseFloat(rating);
      if (location) params.location = location;

      const res = await axios.get("http://localhost:5000/api/restaurants", { params });
      setRestaurants(res.data);
    } catch (err) {
      console.log("API Error:", err);
      setError("Failed to fetch restaurants");
    } finally {
      setLoading(false);
    }
  };

  // Fetch restaurants whenever filters change
  useEffect(() => {
    fetchRestaurants();
  }, [cuisine, rating, location]);

  return (
    <div className="min-h-screen p-10" style={{ backgroundColor: "#B197A4" }}>
      <h1 className="text-center text-4xl font-extrabold mb-10 text-purple-900 tracking-wide">
        BITE BUDDY
      </h1>

      <FilterBar
        cuisine={cuisine}
        setCuisine={setCuisine}
        rating={rating}
        setRating={setRating}
        location={location}
        setLocation={setLocation}
      />

      {loading && <p className="text-center text-lg font-bold">Loading...</p>}
      {error && <p className="text-center text-red-600 font-bold">{error}</p>}

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8 justify-items-center">
        {restaurants.length === 0 && !loading && !error ? (
          <p className="col-span-full text-center text-lg font-bold">No restaurants found.</p>
        ) : (
          restaurants.map((rest) => <RestaurantCard key={rest._id} restaurant={rest} />)
        )}
      </div>
    </div>
  );
};

export default RestaurantListing;
