import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

export default function OrderDetails() {
  const { orderId } = useParams();
  const navigate = useNavigate();

  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const res = await axios.get(`http://localhost:9169/api/orders/order/${orderId}`);
        console.log("Order Data:", res.data);
        setOrder(res.data); // Use res.data if backend returns order directly
      } catch (err) {
        console.error("Failed to fetch order:", err);
        alert("Failed to load order details");
      } finally {
        setLoading(false);
      }
    };

    fetchOrder();
  }, [orderId]);

  const confirmOrder = async () => {
    try {
      await axios.post(`http://localhost:9169/api/orders/reorder/${orderId}`);
      alert("Order confirmed!");
      navigate("/orders");
    } catch (err) {
      console.error(err);
      alert("Failed to confirm order");
    }
  };

  if (loading) return <p className="p-6">Loading order details...</p>;
  if (!order) return <p className="p-6 text-red-500">Order not found!</p>;

  return (
    <div className="min-h-screen bg-[#B197A4] p-6">
      <div className="max-w-xl mx-auto bg-[#dfdfe2] p-6 rounded-md shadow-md">
        <h1 className="text-2xl font-bold mb-2">{order.restaurantName}</h1>
        <p className="mb-1">Order ID: {order.orderId}</p>
        <p className="mb-1">Order Date: {new Date(order.orderDate).toLocaleString()}</p>
        <p className="mb-1">Payment Method: {order.paymentMethod}</p>
        <p className="mb-2">Status: {order.status}</p>

        <h2 className="font-semibold mb-1">Items:</h2>
        {order.items.length === 0 ? (
          <p className="text-gray-600 mb-4">No items in this order.</p>
        ) : (
          <ul className="mb-4">
            {order.items.map((item, i) => (
              <li key={i} className="flex justify-between border-b py-2">
                <span>{item.item} Ã— {item.qty}</span>
                <span>${item.price}</span>
              </li>
            ))}
          </ul>
        )}

        <p className="font-bold text-lg mb-4">Total: ${order.totalPrice}</p>

        <button
          onClick={confirmOrder}
          className="mt-2 bg-[#6b6363] text-white px-6 py-2 rounded-md border-2 border-black"
        >
          CONFIRM ORDER
        </button>
      </div>
    </div>
  );
}
