import React from "react";

const OrderCard = ({ order, onReorder }) => {
  const formattedDate = new Date(order.orderDate).toLocaleDateString("en-US", {
    year: "numeric",
    month: "long",
    day: "numeric",
  });

  return (
    <div className="bg-[#e3e2e3] px-6 py-4 rounded-md shadow flex justify-between items-center">
      {/* Left Side Info */}
      <div>
        <h2 className="text-lg font-bold uppercase">{order.restaurantName}</h2>
        <p className="text-xs text-gray-700">{formattedDate}</p>
        <p className="font-semibold mt-1">${order.totalPrice}</p>
      </div>

      {/* Reorder Button */}
      <button
        onClick={onReorder}
        className="px-5 py-2 bg-gray-700 text-white rounded-md hover:bg-gray-900 transition"
      >
        REORDER
      </button>
    </div>
  );
};

export default OrderCard;
